function positionLegendAnnotation(fixToTop){var $window=$(window);var $sidebar=$(".page_info_margin");if($window.width()>860&&fixToTop){var $footer=$("#epmc_pageFooter");var scrollTop=$window.scrollTop(),footerOffset=$footer.offset().top,distance=(footerOffset-scrollTop);var right=Math.max(0,($window.width()-$(".epmc_pageHolder").width())/2);if(distance<=$sidebar.height()+40){var distanceFromBottom=document.body.scrollHeight-window.innerHeight-window.scrollY;$sidebar.css({position:"fixed",bottom:$footer.height()-distanceFromBottom,top:"auto",right:right+"px"});}else{var distance=$window.scrollTop();if(distance>250){$sidebar.css({position:"fixed",top:"0px",bottom:"auto",right:right+"px"});}else{$sidebar.css("position","static");}}}else{$sidebar.css("position","static");}}